import React from "react";
import { Link } from "react-router-dom";
import FeatureCard from "../components/FeatureCard";

const Home = () => {
  const heroStyle = {
    position: "relative",
    minHeight: "80vh",
    background: "url('/hero-bg.jpg') center/cover no-repeat",
    display: "flex",
    alignItems: "center",
    width: "100%",
  };

  const overlayStyle = {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0,0,0,0.5)",
  };

  const heroContentStyle = {
    position: "relative",
    zIndex: 1,
    textAlign: "center",
    color: "#fff",
    width: "100%",
    padding: "1rem",
  };

  const featuresSectionStyle = {
    padding: "2rem 1rem",
    backgroundColor: "#f7f7f7",
    textAlign: "center",
    width: "100%",
  };

  const featuresGridStyle = {
    display: "flex",
    flexWrap: "wrap",
    gap: "1rem",
    justifyContent: "center",
    width: "100%",
  };

  return (
    <>
      <div style={heroStyle}>
        <div style={overlayStyle}></div>
        <div style={heroContentStyle}>
          <h2 style={{ fontWeight: 600 }}>Welcome to AI Voice Chat</h2>
          <h5 style={{ marginBottom: "1rem" }}>
            Speak naturally, let the AI respond instantly
          </h5>
          <Link to="/chat" style={{ textDecoration: "none" }}>
            <button
              style={{
                padding: "0.75rem 1.5rem",
                backgroundColor: "#1976d2",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Get Started
            </button>
          </Link>
        </div>
      </div>
      <div style={featuresSectionStyle}>
        <h3>Why Use AI Voice Chat?</h3>
        <p style={{ marginBottom: "1rem" }}>
          Experience real-time speech recognition and AI responses for more
          interactive communication.
        </p>
        <div style={featuresGridStyle}>
          <FeatureCard
            title="Natural Speech"
            description="Speak freely and let the AI handle the rest."
            image="/feature1.png"
          />
          <FeatureCard
            title="Real-Time Responses"
            description="Get instant feedback for a more fluid conversation."
            image="/feature2.png"
          />
          <FeatureCard
            title="Easy to Use"
            description="No complicated setup. Just click, talk, and go."
            image="/feature3.png"
          />
        </div>
      </div>
    </>
  );
};

export default Home;
