import React, { useState, useEffect, useCallback } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";

const VoiceChat = () => {
  const [audioUrl, setAudioUrl] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [hasInteracted, setHasInteracted] = useState(false);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const fetchAndPlayAudio = useCallback(() => {
    if (!transcript.trim()) return;

    fetch("http://localhost:3000/api/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: transcript }),
    })
      .then((res) => res.blob())
      .then((blob) => {
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);

        SpeechRecognition.stopListening();
        setIsPlaying(true);

        const audio = new Audio(url);
        audio.play().catch((err) => console.error("Audio play error:", err));

        audio.onended = () => {
          setIsPlaying(false);
          resetTranscript();
          SpeechRecognition.startListening({ continuous: false });
        };
      })
      .catch((error) => console.error("Error fetching audio:", error));
  }, [transcript, resetTranscript]);

  useEffect(() => {
    if (!listening && transcript.trim() !== "" && !isPlaying) {
      fetchAndPlayAudio();
    }
  }, [listening, transcript, isPlaying, fetchAndPlayAudio]);

  useEffect(() => {
    if (hasInteracted && !isPlaying) {
      SpeechRecognition.startListening({ continuous: false });
    }
  }, [hasInteracted, isPlaying]);

  if (!browserSupportsSpeechRecognition) {
    return (
      <div
        style={{
          padding: "2rem",
          textAlign: "center",
          border: "1px solid #ddd",
          borderRadius: "4px",
        }}
      >
        <h3 style={{ color: "red" }}>
          Your browser does not support speech recognition.
        </h3>
      </div>
    );
  }

  if (!hasInteracted) {
    return (
      <div
        style={{
          padding: "2rem",
          textAlign: "center",
          border: "1px solid #ddd",
          borderRadius: "4px",
        }}
      >
        <h2>AI Voice Chat</h2>
        <button
          onClick={() => {
            setHasInteracted(true);
            SpeechRecognition.startListening({ continuous: false });
          }}
          style={{
            padding: "0.75rem 1.5rem",
            backgroundColor: "#1976d2",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Start Chat
        </button>
      </div>
    );
  }

  return (
    <div
      style={{
        padding: "2rem",
        marginTop: "1rem",
        border: "1px solid #ddd",
        borderRadius: "4px",
      }}
    >
      <h2 style={{ textAlign: "center" }}>AI Voice Chat</h2>
      <p style={{ textAlign: "center" }}>
        <strong>Listening:</strong> {listening ? "Yes" : "No"}
      </p>
      <p style={{ textAlign: "center" }}>
        <strong>You said:</strong> {transcript}
      </p>
      {audioUrl && (
        <div style={{ marginTop: "1rem", textAlign: "center" }}>
          <h4>AI Response:</h4>
          <audio src={audioUrl} controls autoPlay style={{ width: "100%" }} />
        </div>
      )}
    </div>
  );
};

export default VoiceChat;
